export function TechStackGrid({
  technologies,
  showProficiency = true,
  showCategories = true,
  className = '',
  onTechClick
}: TechStackGridProps) {
  // Group technologies by category
  const categorizedTech = technologies.reduce((acc, tech) => {
    if (!acc[tech.category]) {
      acc[tech.category] = [];
    }
    acc[tech.category].push(tech);
    return acc;
  }, {} as Record<TechCategory, Technology[]>);

  return (
    <div className={`space-y-8 ${className}`}>
      {showCategories ? (
        // Grouped by category
        Object.entries(categorizedTech).map(([category, techs]) => (
          <div key={category} className="space-y-4">
            {/* Category header */}
            <div className="flex items-center gap-2">
              <div 
                className="h-3 w-3 rounded-full" 
                style={{ 
                  backgroundColor: techCategories[category as TechCategory]?.color || '#94a3b8'
                }}
              />
              <h3 className="text-sm font-semibold text-gray-700">
                {techCategories[category as TechCategory]?.label || category}
              </h3>
              <div className="flex-grow h-px bg-gray-200" />
            </div>
            
            {/* Technology grid */}
            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
              {techs.map((tech, idx) => (
                <motion.div
                  key={`${category}-${tech.name}-${idx}`}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3, delay: 0.1 * idx }}
                  className="bg-white/80 backdrop-blur-sm rounded-lg p-4 border border-gray-100 shadow-sm flex flex-col items-center justify-between hover:shadow-md transition-all duration-300 cursor-pointer"
                  whileHover={{ y: -5, scale: 1.02 }}
                  onClick={() => onTechClick && onTechClick(tech)}
                  style={{ borderTopColor: techCategories[category as TechCategory]?.color || '#94a3b8' }}
                >
                  {/* Icon */}
                  <div className="mb-3 text-gray-700">
                    {tech.icon || getTechIcon(tech.name)}
                  </div>
                  
                  {/* Name */}
                  <div className="font-medium text-sm text-center text-gray-800 mb-2">
                    {tech.name}
                  </div>
                  
                  {/* Proficiency bar */}
                  {showProficiency && tech.proficiency !== undefined && (
                    <div className="w-full">
                      <div className="h-1.5 w-full bg-gray-200 rounded-full overflow-hidden">
                        <motion.div 
                          initial={{ width: 0 }}
                          animate={{ width: `${tech.proficiency}%` }}
                          transition={{ duration: 1, delay: 0.2 + (0.05 * idx) }}
                          className="h-full rounded-full"
                          style={{ 
                            backgroundColor: techCategories[category as TechCategory]?.color || '#94a3b8'
                          }}
                        />
                      </div>
                    </div>
                  )}
                </motion.div>
              ))}
            </div>
          </div>
        ))
      ) : (
        // Simple grid without categories
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
          {technologies.map((tech, idx) => (
            <motion.div
              key={`${tech.name}-${idx}`}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.1 * idx }}
              className="bg-white/80 backdrop-blur-sm rounded-lg p-4 border border-gray-100 shadow-sm flex flex-col items-center justify-between hover:shadow-md transition-all duration-300 cursor-pointer"
              whileHover={{ y: -5, scale: 1.02 }}
              onClick={() => onTechClick && onTechClick(tech)}
              style={{ borderTopColor: techCategories[tech.category]?.color || '#94a3b8' }}
            >
              {/* Icon */}
              <div className="mb-3 text-gray-700">
                {tech.icon || getTechIcon(tech.name)}
              </div>
              
              {/* Name */}
              <div className="font-medium text-sm text-center text-gray-800 mb-2">
                {tech.name}
              </div>
              
              {/* Proficiency bar */}
              {showProficiency && tech.proficiency !== undefined && (
                <div className="w-full">
                  <div className="h-1.5 w-full bg-gray-200 rounded-full overflow-hidden">
                    <motion.div 
                      initial={{ width: 0 }}
                      animate={{ width: `${tech.proficiency}%` }}
                      transition={{ duration: 1, delay: 0.2 + (0.05 * idx) }}
                      className="h-full rounded-full"
                      style={{ 
                        backgroundColor: techCategories[tech.category]?.color || '#94a3b8'
                      }}
                    />
                  </div>
                </div>
              )}
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
}